import type { EditPluginState, IEditor, PluginEvent, PluginWithState } from 'roosterjs-editor-types';
/**
 * @internal
 * Edit Component helps handle Content edit features
 */
export default class EditPlugin implements PluginWithState<EditPluginState> {
    private editor;
    private state;
    /**
     * Construct a new instance of EditPlugin
     * @param options The editor options
     */
    constructor();
    /**
     * Get a friendly name of  this plugin
     */
    getName(): string;
    /**
     * Initialize this plugin. This should only be called from Editor
     * @param editor Editor instance
     */
    initialize(editor: IEditor): void;
    /**
     * Dispose this plugin
     */
    dispose(): void;
    /**
     * Get plugin state object
     */
    getState(): EditPluginState;
    /**
     * Handle events triggered from editor
     * @param event PluginEvent object
     */
    onPluginEvent(event: PluginEvent): void;
}
